mapper.insert:
INSERT INTO board
(
	boardnum
	, menu_name
	, title
	, text

	, originalfile
	, savedfile	

)
VALUES
(
	board_seq.nextval
	, #{menu_name}
	, #{title}
	, #{text}

	, #{originalfile}
	, #{savedfile}

)

mapper.update:
UPDATE board
SET
	title = #{title}
	, text = #{text}
	, regdate = sysdate


	, originalfile = #{originalfile}
	, savedfile = #{savedfile}



	, originalfile = ''
	, savedfile = ''

WHERE
	boardnum = #{boardnum}
